module.exports = function getDynamicFields({ model }) {
  const pluginFiles = cms.getList('PluginFile');
  const pluginFile = pluginFiles.find(p => {
    if (!p.loader) return false;
    if (p.loader.type !== "vue-component-loader") return false;
    if (p.loader.name === model.component) return true;
    return false;
  })
  if (!pluginFile.loader.form) return [];
  return cms.dynamicFormResolver(pluginFile.loader.form);
}

